INSERT INTO `contents` (`ID`, `CONTENT_INDEX`, `ITEM_INDEX`, `SRC`, `CONTENT`, `TAG`) VALUES
(1, 1, 1, '', 'Bạn đ&atilde; từng \"đổ li&ecirc;u xi&ecirc;u\" trước rất nhiều c&aacute;nh đồng n&ocirc;ng sản nổi tiếng? Từ đồi ch&egrave; Th&aacute;i Nguy&ecirc;n, vườn d&acirc;u Mộc Ch&acirc;u đến những thửa ruộng bậc thang tr&ecirc;n đất M&ugrave; Cang Chải? Nếu vậy th&igrave; điểm đến tiếp theo đ&acirc;y chắc chắn sẽ khiến bạn kh&ocirc;ng thể đợi chờ. Đ&oacute; l&agrave; một n&ocirc;ng trại dứa đẹp ng&acirc;y ngất, nằm gọn tr&ecirc;n ngọn đồi Tam Điệp của tỉnh Ninh B&igrave;nh.&nbsp;', 'p'),
(2, 2, 1, '', 'Định vị c&aacute;nh đồng dứa &ldquo;trong truyền thuyết&rdquo;', 'h4'),
(3, 3, 1, '', 'Nh&acirc;n tố &ldquo;g&acirc;y sốt x&igrave;nh xịch&rdquo; trong l&agrave;ng sống ảo Ninh B&igrave;nh hiện nay kh&ocirc;ng g&igrave; kh&aacute;c ch&iacute;nh l&agrave; c&aacute;nh đồng dứa Tam Điệp thuộc n&ocirc;ng trường Đồng Giao. Với khoảng c&aacute;ch chỉ chừng 100km, từ H&agrave; Nội, bạn c&oacute; thể dễ d&agrave;ng di chuyển đến địa điểm n&agrave;y bằng xe m&aacute;y trong khoảng hơn 2 tiếng.', 'p'),
(4, 4, 1, '/image/item1/pic5.png', '', 'img'),
(5, 5, 1, '', '@dangquanluan', 'p'),
(6, 6, 1, '', 'V&igrave; lộ tr&igrave;nh H&agrave; Nội - Ninh B&igrave;nh kh&aacute; đơn giản n&ecirc;n ch&uacute;ng m&igrave;nh sẽ chỉ m&ocirc; tả kỹ hơn qu&atilde;ng đường từ trung t&acirc;m Tam Điệp về đến đồi dứa nổi tiếng. Cụ thể, sau khi đặt ch&acirc;n tới th&agrave;nh phố, bạn đi chếch hướng đ&ocirc;ng l&ecirc;n đường Quang Trung, b&aacute;m s&aacute;t quốc lộ 1A về ph&iacute;a Quang Sơn. Chạy một đoạn th&igrave; rẽ phải tại cửa h&agrave;ng sơn Ph&uacute;c Chi v&agrave;o nh&aacute;nh Hồ Xu&acirc;n Hương - Đặng Tiến Đ&ocirc;ng. Đi hết đường, bạn rẽ tr&aacute;i l&agrave; tới Đồng Giao. Từ đ&acirc;y, chỉ cần hỏi thăm người d&acirc;n địa phương, ch&uacute;ng ta sẽ dễ d&agrave;ng t&igrave;m được n&ocirc;ng trại dứa Tam Điệp.', 'p'),
(7, 7, 1, '', 'Kh&aacute;m ph&aacute; đồi dứa &ldquo;đẹp như mơ&rdquo;&nbsp;', 'h4'),
(8, 8, 1, '', 'D&ugrave; được gọi bằng nhiều c&aacute;i những kh&aacute;c nhau như c&aacute;nh đồng dứa, c&aacute;nh đồng dứa Tam Điệp, n&ocirc;ng trại dứa Đồng Giao nhưng thực chất điểm đến của ch&uacute;ng ta vẫn chỉ c&oacute; một. Đ&oacute; l&agrave; d&atilde;y đồi tr&ugrave;ng điệp với h&agrave;ng chục ng&agrave;n kh&oacute;m dứa trồng th&agrave;nh từng h&agrave;ng chạy d&agrave;i t&iacute;t tắp.', 'p'),
(9, 9, 1, '/image/item1/pic10.png', '', 'img'),
