INSERT INTO `contents` (`ID`, `CONTENT_INDEX`, `ITEM_INDEX`, `SRC`, `CONTENT`, `TAG`) VALUES
(298, 3, 7, '', 'Xuất hiện trong nhiều &aacute;ng văn về m&oacute;n ngon H&agrave; Nội, &ocirc; mai từ l&acirc;u đ&atilde; được xem như đại diện ti&ecirc;u biểu cho tinh hoa ẩm thực của người Tr&agrave;ng An. Cũng bởi vậy, n&oacute; trở th&agrave;nh thức qu&agrave; m&agrave; người ta lu&ocirc;n kiếm t&igrave;m sau mỗi h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; thủ đ&ocirc;.', 'p'),
(299, 4, 7, '/image/item7/pic5.png', '', 'img'),
(300, 5, 7, '', 'Kh&ocirc;ng ngoa khi n&oacute;i rằng tự &ocirc; mai cũng c&oacute; thể tạo th&agrave;nh một &ldquo;vũ trụ&rdquo; m&oacute;n ngon ri&ecirc;ng', 'p'),
(301, 6, 7, '', 'Đặc trưng nổi bật nhất của &ocirc; mai c&oacute; lẽ l&agrave; sự đa dạng đến ngỡ ng&agrave;ng. Người ta c&oacute; thể chế biến thức qu&agrave; n&agrave;y với v&ocirc; v&agrave;n nguy&ecirc;n liệu, từ mơ, mận tới khế, c&oacute;c, với đủ hương vị chua, cay, mặn, ngọt v&agrave; theo đủ kiểu dạng kh&ocirc;, ướt, dẻo, dai. Cộng th&ecirc;m ưu điểm l&agrave; gi&aacute; cả phải chăng c&ugrave;ng c&aacute;ch thức bảo quản đơn giản, &ocirc; mai dễ d&agrave;ng chiếm được t&igrave;nh cảm của v&ocirc; v&agrave;n thực kh&aacute;ch.&nbsp;', 'p'),
(302, 7, 7, '/image/item7/pic8.png', '', 'img'),
(303, 8, 7, '', '&Ocirc; mai sấu - m&oacute;n ngon H&agrave; Nội khiến bao người thương nhớ', 'p'),
(304, 9, 7, '', 'D&ugrave; c&oacute; v&ocirc; v&agrave;n kiểu loại nhưng nhắc đến &ocirc; mai H&agrave; Nội, ch&uacute;ng m&igrave;nh kh&ocirc;ng thể kh&ocirc;ng kể đến sấu non chua ngọt. Những tr&aacute;i sấu gi&ograve;n gi&ograve;n h&ograve;a một ch&uacute;t vị cay ấm của gừng chắc chắn sẽ l&agrave; m&oacute;n qu&agrave; l&yacute; tưởng d&agrave;nh cho người th&acirc;n của bạn.', 'p'),
(305, 10, 7, '', 'Để chọn mua &ocirc; mai chuẩn vị H&agrave; th&agrave;nh, du kh&aacute;ch c&oacute; thể dạo qua c&aacute;c cửa h&agrave;ng gia truyền nổi tiếng, như: &Ocirc; mai Hồng Lam, số 11 H&agrave;ng Đường, &Ocirc; mai Vạn Lợi, số 24 H&agrave;ng Da...', 'p'),
(306, 11, 7, '', '2. Cốm tươi/B&aacute;nh Cốm', 'h4'),
(307, 12, 7, '', 'Nếu c&oacute; dịp du lịch H&agrave; Nội trong những ng&agrave;y thu m&aacute;t mẻ, bạn nhất định đừng bỏ lỡ cơ hội thưởng thức cốm l&agrave;ng V&ograve;ng - một thức qu&agrave; d&acirc;n gi&atilde; nhưng đầy nhớ thương. Giữa tiết trời dịu ngọt của thủ đ&ocirc;, một nắm cốm thơm bọc gọn trong chiếc l&aacute; sen l&agrave; điều g&igrave; đ&oacute; thật kỳ diệu.', 'p'),
(308, 13, 7, '/image/item7/pic14.png', '', 'img'),
(309, 14, 7, '', 'Thưởng thức vị dẻo thơm của cốm tươi l&agrave; bạn đ&atilde; cảm nhận được một phần m&ugrave;a thu H&agrave; Nội', 'p'),
(310, 15, 7, '', '&nbsp;', 'p'),
(311, 16, 7, '', '&nbsp;', 'p'),
(312, 17, 7, '/image/item7/pic18.png', '', 'img'),
(313, 18, 7, '', 'Thức qu&agrave; d&acirc;n gi&atilde; nhưng đầy nhớ thương', 'p'),
(314, 19, 7, '', 'Ngo&agrave;i cốm tươi, du kh&aacute;ch c&ograve;n c&oacute; thể lựa chọn b&aacute;nh cốm l&agrave;m qu&agrave; cho bạn b&egrave;, người th&acirc;n. Để mua được sản phẩm chất lượng, theo kinh nghiệm du lịch H&agrave; Nội, bạn n&ecirc;n &ldquo;bỏ t&uacute;i&rdquo; một số địa chỉ gia truyền nổi tiếng sau đ&acirc;y: Cốm l&agrave;ng V&ograve;ng b&agrave; Hoản, số 36 ng&otilde; 63 Xu&acirc;n Thủy; B&aacute;nh cốm Nguy&ecirc;n Ninh, số 11 H&agrave;ng Than; B&aacute;nh cốm Bảo Minh số 12 H&agrave;ng Than...', 'p'),
(315, 20, 7, '', '3. B&aacute;nh xu x&ecirc;', 'h4'),
(316, 21, 7, '', 'B&aacute;nh xu x&ecirc; hay b&aacute;nh phu th&ecirc; vốn l&agrave; một đặc sản nổi tiếng của xứ Kinh Bắc. Tuy nhi&ecirc;n, du kh&aacute;ch gh&eacute; thăm H&agrave; Nội ho&agrave;n to&agrave;n c&oacute; thể thưởng thức m&oacute;n ngon n&agrave;y với hương vị chuẩn chỉnh tại con phố H&agrave;ng Than.', 'p'),
(317, 22, 7, '/image/item7/pic23.png', '', 'img'),
(318, 23, 7, '', 'B&aacute;nh xu x&ecirc; gấc với m&agrave;u đỏ b&oacute;ng mướt khiến bao thực kh&aacute;ch m&ecirc; mẩn', 'p'),
(319, 24, 7, '', 'Chỉ cần nh&igrave;n tho&aacute;ng qua những chiếc b&aacute;nh n&uacute;ng n&iacute;nh với lớp vỏ trong mềm đủ m&agrave;u sắc, người ta đ&atilde; kh&oacute; tr&aacute;nh khỏi cảm gi&aacute;c bị m&ecirc; hoặc. Cắn một miếng vừa đủ, bạn sẽ cảm nhận&nbsp; được trọn vẹn vị ngọt ng&agrave;o, thơm b&ugrave;i của đậu xanh, dừa tươi, mứt sen v&agrave; thoảng nhẹ ch&uacute;t hương bưởi&hellip; Ăn một lần l&agrave; nhớ m&atilde;i kh&ocirc;ng th&ocirc;i.', 'p'),
