INSERT INTO `contents` (`ID`, `CONTENT_INDEX`, `ITEM_INDEX`, `SRC`, `CONTENT`, `TAG`) VALUES
(1268, 23, 25, '', 'Kh&ocirc;ng m&ocirc; phỏng 100% phi&ecirc;n bản thật, Lan Quế Phường của Ninh Thuận chỉ t&aacute;i hiện một số chi tiết như m&agrave;u sơn đỏ loang lổ, d&atilde;y lồng đ&egrave;n treo cao hay c&aacute;c bức thư ph&aacute;p. Thế nhưng, đ&oacute; lại ch&iacute;nh l&agrave; những đường n&eacute;t, gam m&agrave;u đắt gi&aacute; nhất tạo n&ecirc;n background đỉnh cao cho c&aacute;c bạn trẻ thoải m&aacute;i pose h&igrave;nh.&nbsp;', 'p'),
(1269, 24, 25, '', '5. Tr&ugrave;ng Sơn Cổ Tự - Thiền Viện Tr&uacute;c L&acirc;m Vi&ecirc;n Ngộ', 'h4'),
(1270, 25, 25, '', 'Nếu bạn đang t&igrave;m kiếm điểm nh&igrave;n để bao qu&aacute;t to&agrave;n cảnh Phan Rang - Ninh Thuận th&igrave; chắc chắn sẽ kh&ocirc;ng c&oacute; một lựa chọn n&agrave;o ho&agrave;n hảo hơn Tr&ugrave;ng Sơn Cổ Tự v&agrave; Thiền Viện Tr&uacute;c L&acirc;m Vi&ecirc;n Ngộ. Với khoảng c&aacute;ch chừng 5km, du kh&aacute;ch sẽ chỉ mất khoảng 10 - 15 ph&uacute;t để di chuyển từ trung t&acirc;m th&agrave;nh phố đến địa điểm n&agrave;y. 2 c&ocirc;ng tr&igrave;nh nằm tựa v&agrave;o nhau đầy vững ch&atilde;i tr&ecirc;n n&uacute;i Đ&aacute; Chồng đ&atilde; tạo th&agrave;nh một tổ hợp kiến tr&uacute;c -&nbsp; t&ocirc;n gi&aacute;o với quy m&ocirc; v&ocirc; c&ugrave;ng ấn tượng.&nbsp;', 'p'),
(1271, 26, 25, '/image/item25/pic27.png', '', 'img'),
(1272, 27, 25, '', '@cuongkhii', 'p'),
(1273, 28, 25, '', 'Được xem l&agrave; khu vực địa linh của tỉnh, dọc theo trục đường dẫn v&agrave;o Tr&ugrave;ng Sơn Cổ Tự - Thiền Viện Tr&uacute;c L&acirc;m Vi&ecirc;n Ngộ, du kh&aacute;ch sẽ bắt gặp nhiều ch&ugrave;a, miếu nhỏ n&eacute;p m&igrave;nh giữa c&acirc;y v&agrave; đ&aacute;. Nếu c&oacute; dịp du lịch Ninh Thuận, bạn nhất định đừng bỏ lỡ cơ hội gh&eacute; thăm!', 'p'),
(1274, 29, 25, '', 'Điểm qua một số &ldquo;thi&ecirc;n đường sống ảo&rdquo; nổi bật của Ninh Thuận, hy vọng b&agrave;i viết sẽ mang đến bạn những gợi &yacute; hay ho cho chuyến du h&iacute; sắp tới. Ch&uacute;c bạn c&oacute; được một lịch tr&igrave;nh trải nghiệm thật sự th&uacute; vị tr&ecirc;n mảnh đất đầy nắng gi&oacute; n&agrave;y.', 'p'),
(1275, 1, 26, '', 'Sapa từ l&acirc;u đ&atilde; l&agrave; điểm dừng ch&acirc;n được rất nhiều kh&aacute;ch du lịch trong v&agrave; ngo&agrave;i nước lựa chọn bởi vừa c&oacute; phong cảnh thi&ecirc;n nhi&ecirc;n hoang sơ, vừa c&oacute; nhiều khu nghỉ dưỡng sang trọng, đẳng cấp m&agrave; nổi bật nhất trong số đ&oacute; ch&iacute;nh l&agrave; Sapa Jade Hill Resort & Spa, nơi được mệnh danh l&agrave; &ldquo;Ch&acirc;u &Acirc;u thu nhỏ giữa n&uacute;i rừng Ho&agrave;ng Li&ecirc;n Sơn&rdquo;. Nếu c&oacute; dự định đặt ph&ograve;ng kh&aacute;ch sạn Sapa, h&atilde;y c&ugrave;ng đột nhập để kh&aacute;m ph&aacute; khu nghỉ dưỡng n&agrave;y bạn nh&eacute;.', 'p'),
